select CONCAT("* [[:d:Q", ips_item_id, ']]') from wb_items_per_site where ips_site_id="skwiki" and ips_site_page in(select page_title from skwiki_p.page where page_id in (select cl_from from skwiki_p.categorylinks where cl_to="Wikipédia:Dobré_články" or cl_to="Wikipédia:Najlepšie_články")) and ips_item_id in(select ips_item_id from wb_items_per_site where ips_site_id="cswiki" and ips_site_page in (select page_title from cswiki_p.page where page_id in(select cl_from from cswiki_p.categorylinks where cl_to="Pahýly"))) limit 10;
